import React,{ useState } from 'react'
// import { Button } from './Button'
import { Link, link } from 'react-router-dom'
import './Navbar.css'
import Dropdown from './Dropdown'

function Navbar(){
  const [click, setClick]= useState(false);
  const [dropdown, setDropdown] = useState(false);

  const handelClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const onMouseEnter = () => {
    if(window.innerWidth < 960){
      setDropdown(false);
    } else{
      serDropdown(true);
    }
  }
  return (
    <>
      <nav className="navbar">
        <Link to='/' className='navbar-logo'>
          EPIC
        </Link>
        <div className='menu-icon' onClick={handelClick}>
          <i className={click ? 'fas fa-times' : 'fas fa-bars'} />

        </div>
        <ul className={click ? 'nav-menu active' : 'nav-menu'}>
          <li className='nav-item'>
            <Link to='/' className='nav-links' onClick={closeMobileMenu}>
              Home
            </Link>
          </li>
          <li className='nav-item'>
            <Link to='/services' className='nav-links' onClick={closeMobileMenu}>
              Services <i className='fas fa-caret-down' />
            </Link>
            {dropdown && <Dropdown />}
          </li>
          <li className='nav-item'>
            <Link to='/about-api' className='nav-links' onClick={closeMobileMenu}>
              About API
            </Link>
          </li>
          <li className='nav-item'>
            <Link to='/travel-dome' className='nav-links' onClick={closeMobileMenu}>
              Travel DOME
            </Link>
          </li>
          <li className='nav-item'>
            <Link to='/know-us' className='nav-links' onClick={closeMobileMenu}>
              Know Us <i className='fas fa-caret-down' />
            </Link>
            {dropdown && <Dropdown />}
          </li>
          <li className='nav-item'>
            <Link to='/contact-us' className='nav-links' onClick={closeMobileMenu}>
              Contact Us
            </Link>
          </li>
          
        </ul>
      </nav>
    </>
  ) ;
}

export default Navbar;
